INSERT INTO `tbl_article_db` (`at_id`, `at_url`, `at_img`, `at_title`, `at_content`, `cg_url`, `at_view`, `at_time_add`, `at_time_edit`) VALUES
(2, 'lpeA83CEko', '183939512920220129_102757_2022.png', 'ประกาศวิทยาลัย เรื่อง การเรียนการสอนด้วยระบบ On-Line', '<p style=\"text-align: center;\"></p><p style=\"text-align: center;\">ประกาศวิทยาลัยเทคโนโลยีพณิชยการหาดใหญ่<br>เรื่อง การเรียนการสอนด้วยระบบ On-Line</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ด้วยสถานการณ์แพร่ระบาดของเชื้อโควิด-๑๙ ในพื้นที่ของจังหวัดสงขลา ยังพบผู้ติดเชื้ออย่างต่อเนื่องจึงส่งผลให้ไม่สามารถจัดการเรียนการสอนในรูปแบบ On-Site ซึ่งวิทยาลัยเทคโนโลยีพณิชยการหาดใหญ่ ได้ตระหนักถึงความปลอดภัยของครู บุคลากรทางการศึกษา นักเรียน นักศึกษา อีกทั้งเพื่อให้การดำเนินการจัดการศึกษาเป็นไปด้วยความเรียบร้อยและมีประสิทธิภาพสอดคล้องกับมาตรการป้องกันการแพร่ระบาดของไวรัสโคโรน่า ๒๐๑๙ (Covid-๑๙) วิทยาลัยฯ จึงขอแจ้งกำหนดการดังนี้</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ๑.&nbsp; วิทยาลัยฯ จัดการเรียนการสอนด้วยระบบ On-Line ตั้งแต่วันที่ ๔ ธันวาคม ๒๕๖๔ เป็นต้นไป จนกว่าจะมีการประกาศเปลี่ยนแปลง วิทยาลัยฯ จะแจ้งให้ทราบตามลำดับต่อไป<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ๒.&nbsp; ในกรณีที่นักเรียน นักศึกษา ไม่มีความพร้อมในด้านอุปกรณ์การเรียน On-Line ที่บ้าน สามารถขอใช้สถานที่และอุปกรณ์ของวิทยาลัยฯ ได้ โดยแจ้งความประสงค์ผ่านอาจารย์ที่ปรึกษา</p><p style=\"text-align: center;\">ประกาศ ณ วันที่&nbsp; ๓&nbsp; ธันวาคม&nbsp; ๒๕๖๔</p><p style=\"text-align: center;\">ว่าที่ร้อยตรี&nbsp; จรินทร์&nbsp; ธรรมรักษ์<br>ผู้อำนวยการวิทยาลัยเทคโนโลยีพณิชยการหาดใหญ่</p><p style=\"text-align: center;\"><a class=\"oajrlxb2 g5ia77u1 qu0x051f esr5mh6w e9989ue4 r7d6kgcz rq0escxv nhd2j8a9 nc684nl6 p7hjln8o kvgmc6g5 cxmmr5t8 oygrvhab hcukyx3x jb3vyjys rz4wbd8a qt6c0cv9 a8nywdso i1ao9s8h esuyzwwr f1sip0of lzcic4wl gpro0wi8 q66pz984 b1v8xokw\" href=\"https://www.facebook.com/hashtag/dbtlearning?__eep__=6&amp;__cft__[0]=AZWNPUlEJyOhwu9nMWFCSrCAthW_LrivSA6sOc_ZMICS1g555Pn7GkmpwdhaYB21ZE7o-BffCW-qQqJJr4xOvvB20rGvVeizMifxpb6by0oAdnBNyEjG0BnagzeP2qy6o9-Mf7b9j2gXN5yLlG4ujAjF8Vj2fxe7CeVf_rRf2OG1Bw&amp;__tn__=*NK-R\" role=\"link\" tabindex=\"0\" style=\"background-color: rgb(255, 255, 255); font-family: Kanit, 